  render() {
    return (
      <div>
        <main>
          <form onSubmit={this.handleSubmit}>
            <div>
              <label>
                List select:
                <select>
                  <option value="valor1" selected>Valor 1</option>
                  <option value="valor2">Valor 2</option>
                  <option value="valor3">Valor 3</option>
                </select>
              </label>
            </div>
            <div>
              <label>
                Login:
                <input type='text' value={this.state.value} onChange={this.handleChange} placeholder='Digite o login' autofocus/>
              </label>
              <label>
                Password:
                <input type='password' placeholder='Digite a senha'/>
              </label>
            </div>

            <div>
              <input type='textarea' maxlength='100' rows='10' cols='50'/>
            </div>
          </form>
        </main>
      </div>
    )
  }
}

export default App;
