import React, { Component } from 'react';

class App extends Component {
  constructor () {
    super();

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange () {

  }


  render() {
    return (
      <div>
        <main>
          <forms>
            <div>
              <label>
                List select:
                <select>
                  <option value="valor1" selected>Valor 1</option>
                  <option value="valor2">Valor 2</option>
                  <option value="valor3">Valor 3</option>
                </select>
              </label>
            </div>
            <div>
              <label>
                Login:
                <input type='text' placeholder='Digite o login' autofocus/>
              </label>
              <label>
                Password:
                <input type='password' placeholder='Digite a senha'/>
              </label>
            </div>

            <div>
              <input type='textarea' maxlength='100' rows='10' cols='50'/>
            </div>
          </forms>
        </main>
      </div>
    )
  }
}

export default App;
